import{createClient as e}from"@libsql/client";import"dotenv/config";let t=e({url:process.env.TURSO_DATABASE_URL,authToken:process.env.TURSO_AUTH_TOKEN});export async function execute(e,n={}){return t.execute({sql:e,args:n}).then(({rows:e})=>e)}export async function executeFirst(e,t={}){return execute(e,t).then(([e])=>e??null)}export async function batch(e){return t.batch(e.map(([e,t])=>({sql:e,args:t})),"write")}