export const supportedLocales=["en","es","fr","it","de","nl","ja","zh","ko"];export const weakLocales=["ja","zh","ko"];export const weakLocalesFull=[...weakLocales,"fr","it","de","nl"];let e=[["en","en_us","USen","EUen"],["es","es_us","USes","EUes"],["fr","fr_us","USfr","EUfr"],["it","EUit"],["de","EUde"],["nl","EUnl"],["ja","jp","JPja"],["zh","ch","ch_tw","ch_zh","CNzh","TWzh","CNzh"],["ko","kr","KRko"]];export function guessLocale(s){return e.find(e=>e.find(e=>e===s))?.[0]}export function simplifyLocales(e){let s=new Set;for(let t of e)if(t.endsWith("_us")){let[n]=t.split("_",2);e.includes(n)||s.add(t)}else s.add(t);return[...s]}