export const supportedLocales=["en","es","fr","it","de","nl","ja","zh","ko"];export const weakLocales=["jp","ch","ch_tw","ch_zh","kr","de","nl"];let e=[["en","en_us","USen","EUen"],["es","es_us","USes","EUes"],["fr","fr_us","USfr","EUfr"],["it","EUit"],["de","EUde"],["nl","EUnl"],["ja","jp","JPja"],["zh","ch","ch_tw","ch_zh","CNzh","TWzh","CNzh"],["ko","kr","KRko"]];export function guessLocale(t){return e.find(e=>e.find(e=>e===t))?.[0]}export function simplifyLocales(e){let t=new Set;for(let n of e)if(n.endsWith("_us")){let[s]=n.split("_",2);e.includes(s)||t.add(n)}else t.add(n);return[...t]}