export const supportedLocales=["en_us","es_us","fr_us","en","es","fr","it","de","nl","jp","ch","ch_tw","kr"];let e=[["en_us","USen"],["es_us","USes"],["fr_us","USfr"],["en","EUen"],["es","EUes"],["fr","EUfr"],["it","EUit"],["de","EUde"],["nl","EUnl"],["jp","JPja"],["ch","CNzh"],["ch_tw","TWzh"],["kr","KRko"]];export function guessLocale(s){return e.find(e=>e.find(e=>e===s))?.[0]}export function simplifyLocales(e){let s=new Set;for(let n of e)if(n.endsWith("_us")){let[t]=n.split("_",2);e.includes(t)||s.add(n)}else s.add(n);return[...s]}